mapper.select:
SELECT * FROM invoice WHERE 1=1
AND invoice_no=#{invoiceNo}
AND date >=#{date1}
AND date <=#{date2}
AND invoice_to=#{invoiceTo}
AND bill_laden=#{billLaden}
AND origin =#{origin}
AND dstn =#{dstn}
AND type =#{type}
AND pay_date >=#{date3}
AND pay_date <=#{date4}

SELECT * FROM deliverynote WHERE 1=1
AND hawb=#{hawb}
AND date >=#{date1}
AND date <=#{date2}
AND type =#{type}

SELECT * FROM invoice WHERE type='purchases'
AND date >#{date1}
  AND date <#{date2}

select invoice_no,total,date,date_add(date,INTERVAL 30 DAY) as duetime from invoice where type='purchases'
and pay_date is null and date_add(date,INTERVAL 60 DAY)>=#{time} and date_add(date,INTERVAL 30 DAY) <#{time}
and invoice_to=#{invoiceTo}

select invoice_no,total,date,date_add(date,INTERVAL 30 DAY) as duetime from invoice where type='purchases'
and pay_date is null and date_add(date,INTERVAL 90 DAY)>=#{time} and date_add(date,INTERVAL 60 DAY) <#{time}
and invoice_to=#{invoiceTo}

select invoice_no,total,date,date_add(date,INTERVAL 30 DAY) as duetime from invoice where type='purchases'
and pay_date is null and date_add(date,INTERVAL 120 DAY)>=#{time} and date_add(date,INTERVAL 90 DAY) <#{time}
and invoice_to=#{invoiceTo}

select invoice_no,total,date,date_add(date,INTERVAL 30 DAY) as duetime from invoice where type='sales'
and pay_date is null and date_add(date,INTERVAL 60 DAY)>=#{time} and date_add(date,INTERVAL 30 DAY) <#{time}
and invoice_to=#{invoiceTo}

select invoice_no,total,date,date_add(date,INTERVAL 30 DAY) as duetime from invoice where type='sales'
and pay_date is null and date_add(date,INTERVAL 90 DAY)>=#{time} and date_add(date,INTERVAL 60 DAY) <#{time}
and invoice_to=#{invoiceTo}

select invoice_no,total,date,date_add(date,INTERVAL 30 DAY) as duetime from invoice where type='sales'
and pay_date is null and date_add(date,INTERVAL 120 DAY)>=#{time} and date_add(date,INTERVAL 90 DAY) <#{time}
and invoice_to=#{invoiceTo}

    select invoice_to as name,count(invoice_to) as num from invoice where type='purchases'
AND year(date) =#{year}
group by invoice_to order by num DESC limit 5

  select  dstn,count(dstn) as num from invoice where type='purchases'
    AND year(date) =#{year}
group by dstn order by num DESC limit 5

select  origin,count(origin) as num from invoice where type='purchases'
AND year(date) =#{year}
group by origin order by num DESC limit 5
